import './App.css';
import React from 'react';
import Navigation from '../Navigation/navigation';
import DoorGame from '../DoorGame/doorGame';
import GameButton from '../GameButton/gameButton';

import robotDoor from './robot_door.svg';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      numClosedDoors: 3,
      currentlyPlaying: false,
      doors: {
        door1: {
          src: robotDoor
        },
        door2: {
          src: robotDoor
        }, 
        door3: {
          src: robotDoor
        }
      }
    };
  }

  render() {
    return (
      <div>
        <Navigation />
        <DoorGame doors={this.state.doors}/>
        <GameButton currentlyPlaying={this.state.currentlyPlaying}/>
      </div>
    );
  }
}

export default App;
