import React from 'react';
import './doorGame.css';

//Imports Pictures for rendering later
import robotDoor from './robot_door.svg';
import beachDoor from './beach_door.svg';
import spaceDoor from './space_door.svg';

const doorImage = {
    robot: robotDoor,
    beach: beachDoor,
    space: spaceDoor
};

class doorGame extends React.Component{
    constructor(props){
        super(props);
    }

    randomChoreDoorGenerator() {
        const randomDoorGenerator = Math.floor(Math.random() * this.state.numClosedDoors);
        if (randomDoorGenerator === 0) {
            //openDoor1 = botDoorPath;
            openDoor2 = beachDoorPath;
            openDoor3 = spaceDoorPath;
        } else if (randomDoorGenerator === 1) {
            openDoor1 = beachDoorPath;
            openDoor2 = botDoorPath;
            openDoor3 = spaceDoorPath;
        } else {
            openDoor1 = beachDoorPath;
            openDoor2 = spaceDoorPath;
            openDoor3 = botDoorPath;
        }
}

    render(){
        return (
            <div className="door-row">
                <img id="door1" className="door-frame" src={this.props.doors.door1.src}/>
                <img id="door2" className="door-frame" src={this.props.doors.door2.src}/>
                <img id="door3" className="door-frame" src={this.props.doors.door3.src}/>
          </div>
        );
    }
};

export default doorGame;
