import React from 'react';
import './gameButton.css';

class GameButton extends React.Component{
    constructor(props){
        super(props);
        this.startGame = this.startGame.bind(this);
    }

    startGame(){
        this.setState({
            numClosedDoors: 3,
            currentlyPlaying: true,
            doors: {
                door1: {
                    src: './robot_door.svg'
                },
                door2: {
                    src: './robot_door.svg'
                },
                door3: {
                    src: './robot_door.svg'
                }
            },
            startButtonText: 'Viel Glück!'
        });
        this.randomChoreDoorGenerator();
    }

    randomChoreDoorGenerator() {
            const randomDoorGenerator= Math.floor(Math.random() * this.state.numClosedDoors);
            if (randomDoorGenerator === 0) {
                openDoor1 = botDoorPath;
                openDoor2 = beachDoorPath;
                openDoor3 = spaceDoorPath;
            } else if (randomDoorGenerator === 1) {
                openDoor1 = beachDoorPath;
                openDoor2 = botDoorPath;
                openDoor3 = spaceDoorPath;
            } else {
                openDoor1 = beachDoorPath;
                openDoor2 = spaceDoorPath;
                openDoor3 = botDoorPath;
            }
    }

    render(){
        return (
            <div id="start" className="start-row" startGame={this.props.currentlyPlaying} onClick={this.startGame}>
                {this.props.buttonText}
            </div>
        );
    }
}

export default GameButton;
