import React from 'react';
import './doorGame.css';

//Imports Pictures for rendering later
import robotDoor from './robot_door.svg';
import beachDoor from './beach_door.svg';
import spaceDoor from './space_door.svg';

const doorImage = {
    robot: robotDoor,
    beach: beachDoor,
    space: spaceDoor
};

class doorGame extends React.Component{
    constructor(props){
        super(props);
        this.handleOnClick = this.handleOnClick.bind(this);
    } 

    handleOnClick(event){
        this.openDoor(event).then(this.handleCheckForGameOver());
    }


    handleCheckForGameOver(){
        this.props.checkIfGameOver();
    }

    openDoor(event){
        let listOfDoors = this.props.doors;
        let chosenDoor = listOfDoors.indexOf(listOfDoors.find(doorKey => (doorKey.id === event.target.id)));
        if(listOfDoors[chosenDoor].open === false){
            this.props.openDoorLogic(event.target.id);
        } else {
            console.log('Error: Door is already opened');
            this.handleCheckForGameOver();
            return;
        }
    }

    render(){
        return (
            <div className="door-row">
                {
                    this.props.doors.map(door => {
                      return <img
                      key={door.id}
                      id={door.id}
                      className="door-frame"
                      src={door.src}
                      isOpen={door.open} 
                      onClick={this.handleOnClick} />
                })
                }
          </div>
        );
    }
};

export default doorGame;
