import React from 'react';
import './gameButton.css';
import doorGame from '../DoorGame/doorGame';

class GameButton extends React.Component{
    constructor(props){
        super(props);
        this.handleGameConfigurations = this.handleGameConfigurations.bind(this);
    }

    handleGameConfigurations(){
        this.setState({
            numClosedDoors: 3,
            currentlyPlaying: true,
            doors: {
                door1: {
                    src: './closed_door.svg',
                    open: false
                },
                door2: {
                    src: './closed_door.svg',
                    open: false
                },
                door3: {
                    src: './closed_door.svg',
                    open: false
                }
            },
            startButtonText: 'Viel Glück!'
        });
    }

    randomChoreDoorGenerator() {
        const randomDoorGenerator = Math.floor(Math.random() * this.state.numClosedDoors);
        if (randomDoorGenerator === 0) {
            this.setState({doors: {door1: {src: doorImage.robot}}});
            this.setState({doors: {door2: {src: doorImage.beach}}});
            this.setState({doors: {door3: {src: doorImage.space}}});
            //openDoor1 = botDoorPath;
            //openDoor2 = beachDoorPath;
            //openDoor3 = spaceDoorPath;
        } else if (randomDoorGenerator === 1) {
            this.setState({doors: {door1: {src: doorImage.beach}}});
            this.setState({doors: {door2: {src: doorImage.robot}}});
            this.setState({doors: {door3: {src: doorImage.space}}});
            //openDoor1 = beachDoorPath;
            //openDoor2 = botDoorPath;
            //openDoor3 = spaceDoorPath;
        } else {
            this.setState({doors: {door1: {src: doorImage.space}}});
            this.setState({doors: {door2: {src: doorImage.beach}}});
            this.setState({doors: {door2: {src: doorImage.robot}}});
            //openDoor1 = beachDoorPath;
            //openDoor2 = spaceDoorPath;
            //openDoor3 = botDoorPath;
        }
}

    render(){
        return (
            <div id="start" className="start-row" startGame={this.props.currentlyPlaying} onClick={this.handleGameConfigurations}>
                {this.props.buttonText}
            </div>
        );
    }
}

export default GameButton;
