import './App.css';
import React from 'react';
import Navigation from '../Navigation/navigation';
import DoorGame from '../DoorGame/doorGame';
import GameButton from '../GameButton/gameButton';

import closedDoor from './closed_door.svg';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      numClosedDoors: 3,
      currentlyPlaying: false,
      doors: [
        {src: closedDoor, open: false},
        {src: closedDoor, open: false},
        {src: closedDoor, open: false}
      ],
      startButtonText: 'Spiel starten'
    };
  }

  render() {
    return (
      <div>
        <Navigation />
        <DoorGame doors={this.state.doors}/>
        <GameButton 
          buttonText={this.state.startButtonText} 
          currentlyPlaying={this.state.currentlyPlaying}
          startGame={this.randomChoreDoorGenerator} />
      </div>
    );
  }
}

export default App;
